/**
 * Adds a dynamically created "card" to the web page
 * using uibuilder's low-code capabilities.
 * Try sending multiple times to see the counter increment.
 */

let cardCounter = context.get('cardCounter') ?? 0

msg = {
    "_ui": [
        {
            "method": "remove",
            "components": [
                "#mycard"
            ]
        },
        {
            "method": "add",
            "parent": "#more",
            "components": [
                {
                    "type": "div",
                    "attributes": {
                        "id": "mycard",
                        "title": "This is my Card",
                        "style": "max-width: 20rem;border:solid silver 1px;margin-bottom:1rem;",
                    },
                    "components": [
                        {
                            "type": "h2",
                            "slot": "A New Card",
                            "attributes": {
                                "class": "complementary",
                                "style": "text-align:center;margin-top:0;"
                            }
                        },
                        {
                            "type": "p",
                            "slot": "Some text in a paragraph."
                        },
                        {
                            "type": "p",
                            "slot": "Another paragraph. Count: " + ++cardCounter
                        }
                    ]
                }
            ],
        }
    ]
}
context.set('cardCounter', cardCounter)
return msg;

